create table "project" ("id" text not null primary key, "name" text not null, "description" text, "status" text not null, "startDate" timestamptz, "endDate" timestamptz, "createdBy" text not null references "user" ("id") on delete cascade, "createdAt" timestamptz default CURRENT_TIMESTAMP not null, "updatedAt" timestamptz default CURRENT_TIMESTAMP not null);

create table "task" ("id" text not null primary key, "title" text not null, "description" text, "status" text not null, "priority" text not null, "projectId" text not null references "project" ("id") on delete cascade, "dueDate" timestamptz, "createdBy" text not null references "user" ("id") on delete cascade, "createdAt" timestamptz default CURRENT_TIMESTAMP not null, "updatedAt" timestamptz default CURRENT_TIMESTAMP not null);
